SELECT [itmMasterSeq]
      ,[compCd]
      ,dbo.FN_GET_COMP_NM(compCd) AS compNm
      ,[itmSeq]
      ,[branchPlant]
      ,[branchPlantNm]
      ,[glClass]
      ,[scndLoc]
      ,[unitPrice]
      ,salesCatalogSect
      ,dbo.FN_GET_CODE_NAME2('SALES_CATALOG_SECT' , salesCatalogSect , #{compCd}) + ' [' + RTRIM(LTRIM(salesCatalogSect)) + ']' AS   salesCatalogSectNm
      ,commodityClass
      ,dbo.FN_GET_CODE_NAME2('COMMODITY_CLASS' 	  , commodityClass , #{compCd}) + ' [' + RTRIM(LTRIM(commodityClass)) + ']' AS commodityClassNm
      ,commoditySubClass
      ,dbo.FN_GET_CODE_NAME2('COMMODITY_SUB_CLASS' , commoditySubClass , #{compCd}) + ' [' + RTRIM(LTRIM(commoditySubClass)) + ']' AS commoditySubClassNm
      ,masterPlanningFamily
      ,dbo.FN_GET_CODE_NAME2('MASTER_PLANNING_FAMILY' , masterPlanningFamily , #{compCd}) + ' [' + RTRIM(LTRIM(masterPlanningFamily)) + ']' as masterPlanningFamilyNm
      ,shippingConditionsCode
      ,dbo.FN_GET_CODE_NAME2('SHIPPING_COMMONDITY_CLASS' , shippingConditionsCode , #{compCd}) + ' [' +  RTRIM(LTRIM(shippingConditionsCode)) + ']' AS shippingConditionsCodeNm
      ,warehouseProcessGrp3
      ,dbo.FN_GET_CODE_NAME2('WAREHOUSE_PROCESS_GRP3' , warehouseProcessGrp3 , #{compCd}) + ' [' + RTRIM(LTRIM(warehouseProcessGrp3))  + ']' AS warehouseProcessGrp3Nm
      ,lotStatusCd
      ,dbo.FN_GET_CODE_NAME2('LOT_STATUS_CD' , lotStatusCd , #{compCd})  + ' [' + RTRIM(LTRIM(lotStatusCd))  + ']'   AS lotStatusCdNm
      ,dbo.FN_GET_CODE_NAME2('LOT_PROCESS_TYPE' , lotProcessType , #{compCd})  + ' [' + RTRIM(LTRIM(lotProcessType))  + ']'  AS lotProcessTypeNm
      ,[shelfLifeDays]
      ,[itmId]
      ,[itmNo]
      ,[constYn]
      ,[shortItemNo]
      ,[scndItemNo]
      ,[thirdItemNo]
      ,[itmNm]
      ,[itmNmEng]
      ,[catalogNm]
      ,[searchText]
      ,stockingType
      ,dbo.FN_GET_CODE_NAME2('STOCKING_TYPE' , stockingType , #{compCd}) + ' [' + RTRIM(LTRIM(stockingType))  + ']'  AS stockingTypeNm
      ,[specStandard]
      ,[specMapping]
      ,[uom]
      ,[uomPurchasing]
      ,lineType
      ,dbo.FN_GET_CODE_NAME2('LINE_TYPE' , lineType , #{compCd}) + ' [' + RTRIM(LTRIM(lineType)) +']' AS lineTypeNm
      ,bulkPackedFg
      ,dbo.FN_GET_CODE_NAME2('BULK_PACKED_FG' , bulkPackedFg , #{compCd}) + ' [' + RTRIM(LTRIM(bulkPackedFg)) + ']'  AS bulkPackedFgNm
      ,[useYn]
      ,[categoryCd1]
      ,[categoryNm1]
      ,[categoryCd2]
      ,[categoryNm2]
      ,[trcategoryCd2]
      ,[wonbuCategoryId1]
      ,[wonbuCategoryId2]
      ,[wonbuCategoryId3]
      ,[usages]
      /*,[fileSeq]*/
      ,inventoryCostLv
      ,dbo.FN_GET_CODE_NAME2('INVENTORY_COST_LV' , inventoryCostLv, #{compCd}) + ' [' + RTRIM(LTRIM(inventoryCostLv))  + ']' AS inventoryCostLvNm
      ,salesPriceLv
      ,dbo.FN_GET_CODE_NAME2('SALES_PRICE_LV' , salesPriceLv, #{compCd}) + ' [' + RTRIM(LTRIM(salesPriceLv))  + ']' AS salesPriceLvNm
      ,purchasePriceLv
      ,dbo.FN_GET_CODE_NAME2('PURCHASE_PRICE_LV' , purchasePriceLv, #{compCd}) + ' [' + RTRIM(LTRIM(purchasePriceLv))  + ']' as purchasePriceLvNm
      ,[itmGubn]
      ,[orgArea]
      ,[maker]
      ,[ioFlag]
      ,[currencyId]
      ,[mroRegYn]
      ,[mroItmCd]
      ,[msdsMngYn]
      ,[msdsDocId]
      ,[msdsReceiptYn]
      ,[msdsNotReceiptRsn]
      /*,[orderFg]*/
      ,[crcd]
      /*,[g2bno]*/
      ,orderPolicyCd
      ,dbo.FN_GET_CODE_NAME2('ORDER_POLICY_CD' , orderPolicyCd, #{compCd}) + ' [' + RTRIM(LTRIM(orderPolicyCd))  + ']'   AS orderPolicyCdNm
      ,[valueOrderPolicy]
      ,issueTypeCd
      ,dbo.FN_GET_CODE_NAME2('ISSUE_TYPE_CD' , issueTypeCd, #{compCd})  + ' [' + RTRIM(LTRIM(issueTypeCd))  + ']'  AS issueTypeCdNm
      ,planningCd
      ,dbo.FN_GET_CODE_NAME2('PLANNING_CD' , planningCd, #{compCd}) + ' [' + RTRIM(LTRIM(planningCd))  + ']'   AS planningCdNm
      ,planningFenceRule
      ,dbo.FN_GET_CODE_NAME2('PLANNING_FENCE_RULE' , planningFenceRule, #{compCd}) + ' [' + RTRIM(LTRIM(planningFenceRule))  + ']'   AS planningFenceRuleNm
      ,[planningFence]
      ,[freezeFence]
      ,[leadtimeLevel]
      ,fixedVariable
      ,dbo.FN_GET_CODE_NAME2('FIXED_VARIABLE' , fixedVariable, #{compCd}) + ' [' + RTRIM(LTRIM(fixedVariable))  + ']'   AS fixedVariable
      ,[multipleOrderQuantity]
      ,[unitsPerContainer]
      ,[safetyStock]
      ,[inputEmpNo]
      ,[inputEmpDt]
      ,[modEmpNo]
      ,[modDt]
  FROM [dbo].[V_ITM] WHERE 1=1
	 AND itmMasterSeq		=#{itmMasterSeq} 	
	 AND compCd		  		=#{compCd}		  	
	 AND itmSeq		  		=#{itmSeq}		  	
	 AND branchPlant		=#{branchPlant}		
	 AND branchPlantNm	    =#{branchPlantNm}	
	 AND glClass		    =#{glClass}		  	
	 AND scndLoc		    =#{scndLoc}		  	
	 AND unitPrice		    =#{unitPrice}		  	
	 AND salesCatalogSect	=#{salesCatalogSect}	
	 AND commodityClass	    =#{commodityClass}	  	
	 AND commoditySubClass	=#{commoditySubClass}	
	 AND masterPlanningFamily	=#{masterPlanningFamily}	
	 AND shippingConditionsCode =#{shippingConditionsCode} 	
	 AND warehouseProcessGrp3	=#{warehouseProcessGrp3}  	
	 AND lotStatusCd		=#{lotStatusCd}		  		
	 AND lotProcessType	  	=#{lotProcessType}	  		
	 AND shelfLifeDays	  	=#{shelfLifeDays}	  		
	 AND itmId		  		=#{itmId}		  			
	 AND itmNo		  		=#{itmNo}		  			
	 AND constYn		 	=#{constYn}		  			
	 AND shortItemNo		=#{shortItemNo}		  		
	 AND scndItemNo	        =#{scndItemNo}	          	
	 AND thirdItemNo		=#{thirdItemNo}		  		
	 AND itmNm	          	=#{itmNm}	          		
	 AND itmNmEng	        =#{itmNmEng}	          	
	 AND catalogNm		    =#{catalogNm}		  		
	 AND searchText	        =#{searchText}	          	
	 AND stockingType		=#{stockingType}		  	
	 AND specStandard		=#{specStandard}		  	
	 AND specMapping		=#{specMapping}		  		
	 AND uom			  	=#{uom}			  			
	 AND uomPurchasing	  	=#{uomPurchasing}	  		
	 AND lineType	        =#{lineType}	           	
	 AND bulkPackedFg	 	=#{bulkPackedFg}	 		
	 AND useYn	          	=#{useYn}	          		
	 AND categoryCd1		=#{categoryCd1}				
	 AND categoryNm1		=#{categoryNm1}				
	 AND categoryCd2		=#{categoryCd2}				
	 AND categoryNm2		=#{categoryNm2}				
	 AND trcategoryCd2	    =#{trcategoryCd2}	  		
	 AND wonbuCategoryId1	=#{wonbuCategoryId1}	  	
	 AND wonbuCategoryId2   =#{wonbuCategoryId2}       	
	 AND wonbuCategoryId3	=#{wonbuCategoryId3}	  	
	 AND usages		  		=#{usages}		  			
	 AND fileSeq		  	=#{fileSeq}		  			
	 AND inventoryCostLv	=#{inventoryCostLv}	  		
	 AND salesPriceLv		=#{salesPriceLv}			
	 AND purchasePriceLv	=#{purchasePriceLv}	  		
	 AND itmGubn		  	=#{itmGubn}		  			
	 AND orgArea		  	=#{orgArea}		  			
	 AND maker		  		=#{maker}		  			
	 AND ioFlag		  		=#{ioFlag}		  			
	 AND currencyId		  	=#{currencyId}				
	 AND mroRegYn		  	=#{mroRegYn}		  		
	 AND mroItmCd		  	=#{mroItmCd}		  		
	 AND msdsMngYn		  	=#{msdsMngYn}		  		
	 AND msdsDocId		  	=#{msdsDocId}		  		
	 AND msdsReceiptYn	  	=#{msdsReceiptYn}	  		
	 AND msdsNotReceiptRsn	=#{msdsNotReceiptRsn}	  	
	 AND orderFg		  	=#{orderFg}		  			
	 AND crcd			  	=#{crcd}					
	 AND g2bno		  		=#{g2bno}		  			
	 AND orderPolicyCd	  	=#{orderPolicyCd}	  		
	 AND valueOrderPolicy	=#{valueOrderPolicy}	  	
	 AND issueTypeCd		=#{issueTypeCd}				
	 AND planningCd		  	=#{planningCd}				
	 AND planningFenceRule	=#{planningFenceRule}	  	
	 AND planningFence		=#{planningFence}	  		
	 AND freezeFence		=#{freezeFence}				
	 AND leadtimeLevel	    =#{leadtimeLevel}	   		
	 AND fixedVariable      =#{fixedVariable}          	
	 AND multipleOrderQuantity  =#{multipleOrderQuantity}  	
	 AND unitsPerContainer	  =#{unitsPerContainer}	
	 AND safetyStock		  =#{safetyStock}		
	 AND inputEmpNo		=#{inputEmpNo}				
	 AND inputEmpDt		=#{inputEmpDt}				
	 AND modEmpNo		=#{modEmpNo}				
	 AND modDt		    =#{modDt}		  			
	ORDER BY inputEmpDt DESC , modDt DESC

SELECT [itmMasterSeq]		AS itmMasterSeq
      ,[itmSeq]				AS itmSeq
      ,MAX(compCd) 			AS compCd
      ,(select COM_NM from TOPS_COMPANY where COM_CD = MAX(compCd)) as compNm
      ,MAX(itmId)           AS itmId
      ,MAX(itmNo)           AS itmNo
      ,MAX(shortItemNo)     AS shortItemNo
      ,MAX(scndItemNo)      AS scndItemNo
      ,MAX(thirdItemNo)     AS thirdItemNo
      ,MAX(itmNm)           AS itmNm
      ,MAX(itmNmEng)        AS itmNmEng
      ,MAX(searchText)      AS searchText
  FROM [dbo].[V_ITM] WHERE 1=1
	 AND itmMasterSeq		=#{itmMasterSeq} 	
	 AND itmSeq		  		=#{itmSeq}		  	
	 AND compCd		  		=#{compCd}		  	
	 AND itmId		  		=#{itmId}		  			
	 AND itmNo		  		=#{itmNo}		  			
	 AND shortItemNo		=#{shortItemNo}		  		
	 AND scndItemNo	        =#{scndItemNo}	          	
	 AND thirdItemNo		=#{thirdItemNo}		  		
	 AND itmNm	          	LIKE '%'+#{itmNm}+'%'	          		
	 AND itmNmEng	        =#{itmNmEng}	          	
	 AND stockingType		=#{stockingType}		  	
	GROUP BY itmMasterSeq  , itmSeq
	ORDER BY max(inputEmpDt) desc , max(modDt) desc

SELECT itmMasterSeq
      ,compCd
      ,itmSeq
      ,branchPlant
      ,branchPlantNm
      ,itmId
      ,itmNo
      ,constYn
      ,shortItemNo
      ,scndItemNo
      ,thirdItemNo
      ,itmNm
      ,itmNmEng
      ,inputEmpNo
      ,'' AS inputEmpNm
  FROM [dbo].[V_ITM] WHERE 1=1
	 AND itmMasterSeq		=#{itmMasterSeq} 	
	 AND compCd		  		=#{compCd}		  	
	 AND itmSeq		  		=#{itmSeq}		  	
	 AND branchPlant		=#{branchPlant}		
	 AND branchPlantNm	    =#{branchPlantNm}	
	 AND itmId		  		=#{itmId}		  	
	 AND itmNo		  		=#{itmNo}		  	
	 AND constYn		 	=#{constYn}		  	
	 AND shortItemNo		=#{shortItemNo}		
	 AND scndItemNo	        =#{scndItemNo}	    
	 AND thirdItemNo		=#{thirdItemNo}		
	 AND itmNm	          	=#{itmNm}	        
	 AND itmNmEng	        =#{itmNmEng}	    
	 AND inputEmpNo			=#{inputEmpNo}		
	 AND inputEmpDt			=#{inputEmpDt}		
	 AND modEmpNo			=#{modEmpNo}		
	 AND modDt		    	=#{modDt}

