            ,#{itmNm, mode=IN, jdbcType=NVARCHAR}
            ,#{itmNmEng, mode=IN, jdbcType=NVARCHAR}
            ,#{catalogNm, mode=IN, jdbcType=NVARCHAR}
            ,#{searchText, mode=IN, jdbcType=NVARCHAR}
            ,#{stockingType, mode=IN, jdbcType=NVARCHAR}
            ,#{specStandard, mode=IN, jdbcType=NVARCHAR}
