            ,#{categoryNm2, mode=IN, jdbcType=NVARCHAR}
            ,#{trcategoryCd2, mode=IN, jdbcType=NVARCHAR}
            ,#{wonbuCategoryId1, mode=IN, jdbcType=NVARCHAR}
