            ,#{wonbuCategoryId3, mode=IN, jdbcType=NVARCHAR}
            ,#{usages, mode=IN, jdbcType=NVARCHAR}
            ,#{fileSeq, mode=IN, jdbcType=NVARCHAR}
            ,#{inventoryCostLv, mode=IN, jdbcType=NVARCHAR}
            ,#{salesPriceLv, mode=IN, jdbcType=NVARCHAR}
            ,#{purchasePriceLv, mode=IN, jdbcType=NVARCHAR}
            ,#{itmGubn, mode=IN, jdbcType=NVARCHAR}
            ,#{orgArea, mode=IN, jdbcType=NVARCHAR}
            ,#{maker, mode=IN, jdbcType=NVARCHAR}
